import React, { Component } from 'react';
//import ParticlesBg from 'particles-bg';
import Clarifai from 'clarifai';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import FaceRecognition from './components/FaceRecognition/FaceRecognition'
import './App.css';

const MODEL_ID = 'face-detection';

class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {},
    }
  }

  returnClarifaiRequestOptions = () => {
    // Your PAT (Personal Access Token) can be found in the portal under Authentification
    const PAT = '1adf1b631d4e481a8af0a925956975ae';
    // Specify the correct user_id/app_id pairings
    // Since you're making inferences outside your app's scope

    const USER_ID = 'worbdj200';       
    const APP_ID = 'hyejuapp';

    const IMAGE_URL = this.state.input;

    const raw = JSON.stringify({
        "user_app_id": {
            "user_id": USER_ID,
            "app_id": APP_ID
        },
        "inputs": [
            {
                "data": {
                    "image": {
                        "url": IMAGE_URL
                    }
                }
            }
        ]
    });

    const requestOptions = {
      method: 'POST',
      headers: {
          'Accept': 'application/json',
          'Authorization': 'Key ' + PAT
      },
      body: raw
    };

    return requestOptions
  }


  calculateFaceLocation = (data) => {
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    console.log(width, height);

    return {
      leftCol: clarifaiFace.left_col * width,
      topRow: clarifaiFace.top_row * height,
      rightCol: width - (clarifaiFace.right_col * width),
      bottomRow: height - (clarifaiFace.bottom_row * height) 
    }
  }

  displayFaceBox = (box) => {
    this.setState({box:box});
    console.log(box);
  }
  onInputChange = (event) => {
    this.setState({ input: event.target.value });
    console.log(event.target.value);
  }

  onButtonSubmit = () => {
    console.log('click');
    this.setState({ imageUrl: this.state.input });
    // https://samples.clarifai.com/metro-north.jpg
    // https://th.bing.com/th/id/OIP.zpu9WzA37GAW6hSPWtx3gQHaK0?rs=1&pid=ImgDetMain

    const data = returnClarifaiRequestOptions();
    console.log(data);
    // fetch('http://localhost:3000/imageurl', {
    //     method: 'post',
    //     headers: {'Content-Type': 'application/json'},
    //     body: JSON.stringify({
    //       input: this.state.input
    //     })
    // })
    fetch("https://api.clarifai.com/v2/models/" + MODEL_ID + "/outputs", returnClarifaiRequestOptions())
      .then(response => response.json())
      .then(response => {
          console.log(response.outputs[0].data.regions[0].region_info.bounding_box)
          this.displayFaceBox(this.calculateFaceLocation(response))
        }        
      )
      .catch(error => console.log('error', error));
  }

  render() {
    return (
      <div className="App"> 
        {/* <ParticlesBg className='particlesBg' type="cobweb" num={5} color={["#FFF3D9"]} /> */}
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit}/>
        <FaceRecognition imageUrl={this.state.input} box={this.state.box} />
      </div>
    );
  }
}

export default App;
